import { Card, CardContent, CardMedia, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useSelector } from "react-redux";

function ProductPage() {
  const API = "https://fakestoreapi.com/products/";

  const [products, setProducts] = useState([]);

  const selectedCategory = useSelector((state) => state);
  const SET_SEARCH_QUERY = useSelector((state) => state.searchQuery);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(API);
      console.log("API Response:", response.data);
      setProducts(response.data);
    } catch (err) {
      console.error(err, "error");
    }
  };
  useEffect(() => {
    fetchProducts();
 //   console.log("filteredProductsBySearch:", filteredProductsBySearch);
  }, []);

  const filterProducts = (products) => {
    const filteredByCategory =
      selectedCategory === "All Category"
        ? products
        : products.filter((e) => e.category === selectedCategory);

    const filteredBySearch =
      SET_SEARCH_QUERY === ""
        ? filteredByCategory
        : filteredByCategory.filter((e) =>
            e.title.toLowerCase().includes(SET_SEARCH_QUERY.toLowerCase())
          );

    return filteredBySearch;
  };

  const filteredProductsBySearch = filterProducts(products);

  useEffect(() => {
  // console.log(filteredProductsBySearch.map((e) => e));
    console.log("filteredProductsByCategory:", filteredProductsBySearch);
  }, [products]);

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      {products ? (
        <div className="row" style={{ width: "70%" }}>
          {filteredProductsBySearch.map((e) => {
            return (
              <div className="col-lg-4 col-md-6 col-sm-12">
                <Card sx={{ height: 245, margin: "15px", textAlign: "center" }}>
                  <CardContent>
                    <Typography>{e.title}</Typography>
                  </CardContent>
                  <CardMedia
                    component="img"
                    height="340"
                    image={e.image}
                    alt={e.title}
                    sx={{ objectFit: "cover" }}
                  />
                </Card>
              </div>
            );
          })}
        </div>
      ) : (
        "Loading..."
      )}
    </div>
  );
}

export default ProductPage;
