import React from "react";
import "./Banner.css";
import MenuIcon from "@mui/icons-material/Menu";
import IconButton from "@mui/material/IconButton";
import { Button, MenuItem, Select, TextField, Typography } from "@mui/material";
function Banner() {
  return (
    <div className="banner">
      {/* menu options */}
      <div
        style={{
          width: "70%",
          display: "flex",
          justifyContent: "center",
          backgroundColor: "rgba(43,42,41,255)",
          marginTop: 0,
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-evenly",
            width: "60%",
            alignItems: "center",
            color: "#fff",
          }}
        >
          <p>Best Sellers</p>
          <p>Gift Ideas</p>
          <p>New Releases</p>
          <p>Today's Deals</p>
          <p>Customer Service</p>
        </div>
      </div>
      {/* menu options */}

      <Typography
        sx={{
          marginTop: "2rem",
          fontSize: "3rem",
          color: "#fff",
          fontWeight: "600",
          padding: "1rem",
        }}
      >
        Eflyer
      </Typography>

      {/* Search options */}
      <div className="filter">
        <IconButton color="primary"  sx={{ color: "#fff" }}>
          <MenuIcon />
        </IconButton>
        <Select
          value="All Category"
          size="small"
          sx={{ color: "#fff", backgroundColor: "grey" }}
        >
          <MenuItem value="All Category">All Category</MenuItem>
          <MenuItem value="option1">Option 1</MenuItem>
          <MenuItem value="option2">Option 2</MenuItem>
          <MenuItem value="option3">Option 3</MenuItem>
        </Select>

            <TextField
            size="small"
            sx={{ backgroundColor: "#fff", color: "#000" , borderRadius:'10px', width:'20rem'}}
            outlined={false}
            />
        <Select
          value="English"
          size="small"
          sx={{ color: "#fff", backgroundColor: "grey" }}
        >
          <MenuItem value="English">English</MenuItem>
          <MenuItem value="Hindi">Hindi</MenuItem>
          <MenuItem value="Marathi">Marathi</MenuItem>
        </Select>

        <IconButton color="primary">
          <MenuIcon />
        </IconButton>
        <IconButton color="primary">
          <MenuIcon />
        </IconButton>
      </div>

      {/* Search options */}
    </div>
  );
}

export default Banner;
