import React from "react";
import "./Banner.css";
import MenuIcon from "@mui/icons-material/Menu";
import IconButton from "@mui/material/IconButton";
import {
  Button,
  InputAdornment,
  MenuItem,
  Select,
  TextField,
  Typography,
} from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";
import PersonIcon from "@mui/icons-material/Person";
function Banner() {
  return (
    <div className="banner">
      {/* menu options */}
      <div
        style={{
          width: "70%",
          display: "flex",
          justifyContent: "center",
          backgroundColor: "rgba(43,42,41,255)",
          marginTop: 0,
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-evenly",
            width: "60%",
            alignItems: "center",
            color: "#fff",
          }}
        >
          <p>Best Sellers</p>
          <p>Gift Ideas</p>
          <p>New Releases</p>
          <p>Today's Deals</p>
          <p>Customer Service</p>
        </div>
      </div>
      {/* menu options */}

      <Typography
        sx={{
          marginTop: "2rem",
          fontSize: "3rem",
          color: "#fff",
          fontWeight: "600",
          padding: "1rem",
        }}
      >
        Eflyer
      </Typography>

      {/* Search options */}
      <div className="filter">
        <IconButton color="primary" sx={{ color: "#fff", }}>
          <MenuIcon />
        </IconButton>
        <Select
          value="All Category"
          size="small"
          sx={{ color: "#fff", backgroundColor: "grey" }}
        >
          <MenuItem value="All Category">All Category</MenuItem>
          <MenuItem value="option1">Option 1</MenuItem>
          <MenuItem value="option2">Option 2</MenuItem>
          <MenuItem value="option3">Option 3</MenuItem>
        </Select>

        <TextField
          size="small"
          sx={{
            backgroundColor: "#fff",
            color: "#000",
            borderRadius: "10px",
            width: "29rem",
            marginTop: 0, 
          marginBottom: 0
          }}
          placeholder="Search this blog"
          InputProps={{
            endAdornment: (
              <InputAdornment position="end" size='large' sx={{ height: "100%" }}>
                <div style={{ backgroundColor: "orange" }}>
                  <SearchIcon style={{ color: "#fff" }} />
                </div>
              </InputAdornment>
            ),
          }}
        />
        <Select value="English" size="small" sx={{ backgroundColor: "#fff" }}>
          <MenuItem value="English">English</MenuItem>
          <MenuItem value="Hindi">Hindi</MenuItem>
          <MenuItem value="Marathi">Marathi</MenuItem>
        </Select>

        <Button startIcon={<AddShoppingCartIcon />} sx={{ color: "#fff" }}>
          Cart
        </Button>
        <Button startIcon={<PersonIcon />} sx={{ color: "#fff" }}>
          Cart
        </Button>
      </div>

      {/* Search options */}
    </div>
  );
}

export default Banner;
