import { Card, CardContent, CardMedia, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useSelector } from "react-redux";

function ProductPage() {
  const API = "https://fakestoreapi.com/products/";

  const [products, setProducts] = useState([]);

  const selectedCategory = useSelector(
    (state) => state.product.selectedCategory
  );
  const SET_SEARCH_QUERY = useSelector((state) => state.product.searchQuery);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(API);
      console.log("API Response:", response.data);
      setProducts(response.data);
    } catch (err) {
      console.error(err, "error");
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const filteredByCategory =
    selectedCategory === "All Category"
      ? products
      : products.filter((e) => e.category === selectedCategory);

  const filteredBySearch =
    SET_SEARCH_QUERY === ""
      ? filteredByCategory
      : filteredByCategory.filter(
          (e) =>
            e.title &&
            e.title.toLowerCase().includes(SET_SEARCH_QUERY.toLowerCase())
        );

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      {products.length > 0 ? (
        <div className="row" style={{ width: "70%" }}>
          {filteredBySearch.length > 0 ? (
            filteredBySearch.map((e) => (
              <div className="col-lg-4 col-md-6 col-sm-12" key={e.id}>
                <Card sx={{ height: 245, margin: "15px", textAlign: "center" }}>
                  <CardContent>
                    <Typography>{e.title}</Typography>
                    <div>
                        <Typography>Price: INR {e.price}</Typography>
                    </div>
                  </CardContent>
                  <CardMedia
                    component="img"
                    height="340"
                    image={e.image}
                    alt={e.title}
                    sx={{ objectFit: "cover" }}
                  />
                </Card>
              </div>
            ))
          ) : (
            <div>No products found.</div>
          )}
        </div>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
}

export default ProductPage;
